/* MySQL version: 5.7.34-log */

Use ioforfun;
set names utf8mb4;

drop table if exists tab_user;
drop table if exists tab_favorite;
drop table if exists tab_game_img;
drop table if exists tab_game;
drop table if exists tab_category;
drop table if exists tab_developer;

create table tab_user
(
    user_id                  int not null auto_increment,
    username             varchar(100) not null,
    password             varchar(32) not null,
    name                 varchar(100),
    birthday             date,
    sex                  int default 0, /* 0 is unknown, 1 is man, 2 is woman, 3 is others */
    telephone            varchar(11),
    email                varchar(100),
    status               int default 0, /* 0 is notActivated, 1 activated, 2 is baned*/
    activation_code		 varchar(50),
    primary key (user_id),
    unique(username)
) DEFAULT CHARSET=utf8mb4;



create table tab_category
(
   category_id          int not null auto_increment,
   category_name        varchar(100) not null,
   primary key (category_id),
   unique(category_name)
) DEFAULT CHARSET=utf8mb4;

create table tab_favorite
(
   game_id              int not null,
   date                 date not null,
   user_id                  int not null,
   primary key (game_id, user_id)
) DEFAULT CHARSET=utf8mb4;

create table tab_game
(
   game_id              int not null auto_increment,
   name                 varchar(500) not null,
   rate                 double default 0,
   introduction         varchar(1000),
   isShow               int default 1,
   date                 varchar(19),
   favoriteCount        int default 0,
   clickCount           int default 0,
   category_id          int not null,
   image                varchar(200),
   developer_id         int default 0,
   url                  varchar(50),
   primary key (game_id),
   unique(name)
) DEFAULT CHARSET=utf8mb4;

create table tab_developer
(
   developer_id         int not null auto_increment,
   name                 varchar(200) not null,
   telephone            varchar(20) not null,
   email                varchar(200),
   primary key (developer_id),
   unique(name)
) DEFAULT CHARSET=utf8mb4;

INSERT INTO `tab_category` VALUES (12,'Agario'),(6,'Battle Royale'),(9,'Free-For-All'),(7,'Moomoo'),(13,'Power-ups'),(5,'Shooter'),(11,'Slither'),(14,'Tank'),(8,'Team'),(10,'Upgrades'),(15,'Zombies');

INSERT INTO `tab_game` VALUES (1,'https://smashkarts.io/?iogs',0,'Smash Karts is a Kart Battle Arena game. Drive fast. Fire rockets. Make big explosions.',1,NULL,0,0,13,'Smash Karts.png',0,'https://smashkarts.io/?iogs'),(2,'http://zombs.io',0,'ZOMBS.io is a tower-defense IO game with an amazing mix of Minecraft and zombies. Build your base and defend it against waves of zombies. Upgrade your base and make it stronger. Be careful, every wave becomes stronger and stronger. How many nights can you survive?',1,NULL,0,0,13,'ZOMBS.io.png',0,'http://zombs.io'),(3,'https://stabfish.io/?p=iogs',0,' is a free iogame. This is a game where you are a deadly marine monster that stabs your enemy with a tusk on your head, then decorate your weapon with your victims\' head!',1,NULL,0,0,13,'Stabfish.io.png',0,'https://stabfish.io/?p=iogs'),(4,'https://www.venge.io/',0,'Venge.io is an objective-based first-person shooter. Every match is an intense unique experience with the ability cards that you can get in the game.',1,NULL,0,0,13,'Venge.io.png',0,'https://www.venge.io/'),(5,'https://deeeep.io',0,'Deeeep.io is an ocean-based free for all io game. It can be compared to mope.io in that your main objective is the climb up the food chain and dominate the ocean. You start off underwater as a human and aim to evolve into bigger and stronger animals to increase your chances of survival.',1,NULL,0,0,13,'Deeeep.io.png',0,'https://deeeep.io'),(6,'https://mope.io/?y8BannerModify=1',0,'Mope is a foraging/survival game set in a colorful 2d environment. You start off as a mouse and aim to make your way up the food chain through eating berries to gain xp. Your objective is simply survival, do this by progressing up the food chain and avoiding predators.',1,NULL,0,0,13,'Mope.io.png',0,'https://mope.io/?y8BannerModify=1'),(7,'https://castles.cc',0,'Castles is a free sandbox game. Jump on into this fun sandbox and do whatever you want.',1,NULL,0,0,8,'Castles.cc.png',0,'https://castles.cc'),(8,'https://laserbots.io/',0,'Laserbots is a free IOGame. This is an isometric 3rd person shooter where you play as a virtual robot in a virtual arena. Fight against other players in the PvP deathmatch mode or defend the base from the horde in the co-operative zombies mode.',1,NULL,0,0,8,'laserbots.io.png',0,'https://laserbots.io/'),(9,'https://wormate.io/',0,'Wormate.io is a Slither-style game with a twist. This game features cool power ups, a lot more customisation and awesome food graphics that make them look extra tasty even to the pickiest of worms.',1,NULL,0,0,13,'Wormate.io.png',0,'https://wormate.io/'),(10,'https://violentegg.fun',0,'Shell Shockers is first-person shooter io game in which you\'re fighting as an egg. The world\'s first browser-based First Person Egger! Get cracking! Choose your class and terminate your enemies with eggstreme prejudice in this 3d multplayer shooter. Equip lethal weapons like the Scramble Shotgun and EggK47 as you cluck your way to victory.',1,NULL,0,0,5,'Shell Shockers.png',0,'https://violentegg.fun'),(11,'http://shootz.io',0,'Shootz.io is a multiplayer battle royale arena in which you need to kill red circles and other players. Shoot others or be shot. See who is the strongest in this arena of death.',1,NULL,0,0,11,'Shootz.io.png',0,'http://shootz.io'),(12,'https://rugby-io.com',0,'Rugby,io is the io version of rugby that you can play with your friends. Enter the rugby stadium and face the other teams in a fierce battle Rugby.io! Tackle everyone in sight to move forward and score a Touchdown! Rugby.io Battle Mayhem is a one-touch American Football inspired game!',1,NULL,0,0,8,'Rugby.io.png',0,'https://rugby-io.com'),(13,'https://tacticscore.io',0,'Tactics Core is an online multiplayer MOBA shooter io game based on an RTS game called Strike Tactics.',1,NULL,0,0,5,'Tactics Core.png',0,'https://tacticscore.io'),(14,'https://agnosiagames.com/viruswarriorio',0,'Viruswwarrior.io is a free arena game. In this shooting game, you should fight against a virus pandemic and save the world from a terrible end! You should cure virus patients with your gun which throwing vaccine syringes.',1,NULL,0,0,15,'VirusWarrior.io.png',0,'https://agnosiagames.com/viruswarriorio'),(15,'https://crazysteve.io/',0,'    CrazySteve.io is a team-based survivor game in which you must gather resources and battle enemies to win. The main task in Crazy Steve is to collect as many points as you need to collect apples and diamonds appearing on the map, you get much more experience for diamonds, and thanks to apples you can restore health. a lot of diamonds, you know what you are attracted to this game? Yes, you need to scatter around dynamite pieces, shoot arrows, attack with a sword, shoot fireballs and many other weapons to destroy rivals, and then take their experience to yourself. , which s you can kill and restore his health ',1,NULL,0,0,8,'CrazySteve.io.png',0,'https://crazysteve.io/'),(16,'https://nightpoint.io',0,'Nightpoint.io is a fast-paced multiplayer shooter io game with zombies. Survive the zombie apocalypse online! Choose your skin and level up by shooting zombies to get new better weapons and upgrades such as speed and accuracy. Can you reach the top of the leaderboard?',1,NULL,0,0,5,'nightpoint.io.png',0,'https://nightpoint.io'),(17,'https://evilvil.space',0,' ',1,NULL,0,0,15,'E V I L V I L.png',0,'https://evilvil.space'),(18,'https://crowdcity-io.com',0,'Crowd City.io is an online multiplayer io game where you must turn all the humans into zombies. Play Role of Zombie and Attack the Humans and Make Big Big Crowd Team with Zombies and Be Happy to Smash the Opponent Zombie Teams Be a like Popular Team.',1,NULL,0,0,15,'Crowd City.io.png',0,'https://crowdcity-io.com'),(19,'https://zomcraft.io',0,'In Zomcraft.io you can build houses, walls or traps to prevent monsters from attacking you. Can play alone or in team. This game requires you to farm a rock tree, zombies, monsters to get the ingredients to build a wall playing device, trap weapons, or to cook.',1,NULL,0,0,15,'Zomcraft.io.png',0,'https://zomcraft.io'),(20,'https://crazybattle.fun/',0,'CrazyBattle.fun is a 2-D arena game.',1,NULL,0,0,15,'CrazyBattle.png',0,'https://crazybattle.fun/'),(21,'https://www.modd.io/play/area-51',0,'Area51 Stormers is a racing and action game. When the match begins, players should try to break into Area51 by blowing up the walls and being careful of the guards who will try to catch you. If the player is caught before entering the site, he will be taken to a local prison and will have to be released by the other invaders.',1,NULL,0,0,15,'Area51 Stormers.png',0,'https://www.modd.io/play/area-51'),(22,'https://searide.io',0,'Sea Ride is a free iogame. Welcome to the deepest depths of the farthest sea. A world of pure adventure and imagination. A world of danger. Sea Ride is an iogame where you start small and eat your way to the top. Gobble up other fish and smash open different crates to unlock powerups that let you attack from range. Move fast and be a tactical genius to avoid the other fish as you swim your way.',1,NULL,0,0,12,'Sea Ride.png',0,'https://searide.io'),(23,'https://zombieswithguns.io/',0,'ZombiesWithGuns.io is a deathmatch game where you play as a gun-wielding zombie!',1,NULL,0,0,15,'ZombiesWithGuns.io.png',0,'https://zombieswithguns.io/'),(24,'https://lunarblast-io.com/',0,' Just enter your name, choose your spaceship, and hit that play button to enter into a world of moons and stars to battle against opponent ships. The controls are very simple, ',1,NULL,0,0,12,'Lunarblast.io.png',0,'https://lunarblast-io.com/'),(25,'https://tetrads.io',0,'Tetrads.io is a free iogame. Welcome to the retro-blip animation style of Tetrads.io, a world where you are but a cube in a world of shapes. In order to survive, you\'ll have to seek out other shapes and ad them to your own mass then seek and destroy smaller shapes. Each battle you get into will cost you the average of your blocks and the opposing players blocks when you consume them so choose your victims wisely and always pick on someone smaller than you. Keep this in mind as you move forward through the game because you will always be attacked by players bigger than you. In order to survive and become the top of the leader board. Outsmart other players and make judicious use of your \"boost\" power-up by pressing the left mouse button. This will allow you to speed away from a potential attack, or get the jump on a potential victim. The boost has its own power bar which will quickly run out as you use it and then will slowly re-charge at half the speed.',1,NULL,0,0,12,'Tetrads.io.png',0,'https://tetrads.io'),(26,'https://seaguys.io',0,'Seaguys.io is a free IOGame. Welcome to the underwater arena where players compete to overcome obstacles, challenges and even other players themselves. Collect coins, and points and see if you can take on the world in this fast-paced original style game of wits and reflexes. Play against other players through a series of fast-paced challenges like Deathmatch, Racing, and Battle Royale! Win the challenges to earn coins, win the round to earn a crown! If you feel like you\'re up to the challenge of taking on the whole world, leveling up your character and becoming the ultimate sea person, then this is the game for you.',1,NULL,0,0,12,'Sea Guys.png',0,'https://seaguys.io'),(27,'http://fishz.io',0,'Fishz.io is an evolution io game where your goal is to become the scariest fish in the sea. There\'s no clowning around in the dog eat dog world of vicious fishes! You may begin as a humble Clown fish, but as you upgrade you slowly evolve into a terrifying predator.',1,NULL,0,0,12,'Fishz.io.png',0,'http://fishz.io'),(28,'https://braains2.io/?preload=1',0,'Braains2.io is a zombie survival game where you must fight to prevent further infection! Each server can have up to 50 players, 1 starts as the zombie and his objectives is to spread the infections by tagging the humans. The humans objective of course have to survive the whole match duration without being tagged, its hard to out run them however so it is recommended to barricade yourself somewhere safe with other players.',1,NULL,0,0,15,'Braains2.io.png',0,'https://braains2.io/?preload=1'),(29,'https://hury.io/',0,'Hury.io is a free chicken-style game. Collect eggs, attack other birds!',1,NULL,0,0,12,'hury.io.png',0,'https://hury.io/'),(30,'http://megaspin.io',0,'MegaSpin.io is a multiplayer fidget spinner combat game. The goal of the game is to collect gems and dominate another players. You are provided with a selectable collection of different spinners and emojis which can all be used and customized from the beginning without reaching any rank.',1,NULL,0,0,12,'MegaSpin.io.png',0,'http://megaspin.io'),(31,'https://snowwars.io',0,'Snowwars.io is a fast-paced Agar style game that drops you into the center of an ice-slicked arena where only the strong survive.',1,NULL,0,0,12,'Snowwars.io.png',0,'https://snowwars.io'),(32,'http://painty.io',0,'Painty.io is a 2D Shooter IO game giving you a bunch of amusing hours as well as nice challenges to conquer. Just step into this game arena now then prove all of your skills to the whole world. You have to aim and shoot paint at the enemies in your sight by using your gun, collect food to earn your experience, fuel, increase your points so that you can upgrade your stats.',1,NULL,0,0,12,'Painty.io.png',0,'http://painty.io'),(33,'http://popsplit.us',0,'Popsplit.io is a super fun battle royal game in which you are competing against loads of other online players in a game that will test your skill and tactical know-how. You will start with a small cell, you must quickly absorb the smaller randomly generated cells that are positioned around the map to grow in size. ',1,NULL,0,0,12,'Popsplit.us.png',0,'http://popsplit.us'),(34,'http://oib.io',0,'Oib.io is a micro gestion and strategy io game. ',1,NULL,0,0,12,'Oib.io.png',0,'http://oib.io'),(35,'https://kazap.io',0,'Kazap.io is a fast-action 2D dogfighting game where you collect energy to grow in power. Join your friends to take down larger players quickly, or test your skills as a lone wolf and survive as long as possible!',1,NULL,0,0,12,'Kazap.io.png',0,'https://kazap.io'),(36,'https://yumy.io/',0,' ',1,NULL,0,0,12,'yumy.io.png',0,'https://yumy.io/'),(37,'http://zorb.io',0,'Zorb.io is an agario like game with an awesome new perspective, it takes the generic 2d io game and translates it into a 3d environment with 360 degree movement!',1,NULL,0,0,12,'Zorb.io.png',0,'http://zorb.io'),(38,'https://florr.io/',0,'Fight other flowers for garden dominance',1,NULL,0,0,12,'florr.io.png',0,'https://florr.io/'),(39,'https://www.bighero.io/',0,'Bighero.io is a multiplayer action game where when you kill someone they turn into dots of nourishment.',1,NULL,0,0,12,'Bighero.io.png',0,'https://www.bighero.io/'),(40,'http://senpa.io/web',0,'Senpa.io is a super fun battle royale game in which you are competing against loads of other online players in a game that will test your skill and tactical know how. You will start with a small cell, you must quickly absorb the smaller randomly generated cells that are positioned around the map to grow in size, its a similar concept to Agar.io as its made by the same developer but has alot of extra cool features!. ',1,NULL,0,0,12,'Senpa.io.png',0,'http://senpa.io/web'),(41,'https://ecapsgames.com/fidgetio-com/fullscreen/',0,'1. Collect score, to increase your spinner power.',1,NULL,0,0,12,'Fidgetio.com.png',0,'https://ecapsgames.com/fidgetio-com/fullscreen/'),(42,'https://germs.io',0,'Germs.io is a free eat-to-grow io game in which you\'re a germ trying to consume your entire petri dish. The longest running, and most popular Agario style game since Agar itself. Come for the action, stay for the updates! Become a germ that survives by devouring other germs. Upgrade your abilities and customize your appearence.',1,NULL,0,0,12,'Germs.io.png',0,'https://germs.io'),(43,'https://sharkattack-io.com',0,'SharkAttack.io is a fun arena game. Everyone loves to say there are plenty of fish in the sea but what they don\'t mention is that many of those fishes are vicious. You may think you\'re fast, you may think you\'re big and powerful but as a wise old Jedi once said: \"there\'s always a bigger fish.\" Perhaps with patience, practice, and skill that bigger fish will be you. In SharkAttack.io you\'re either big or you\'re dead. Increase your size and speed by gobbling up plankton and smaller bottow dwelling fish then use your increased size to hunbt down and gobble up other playres. It\'s a shark eat shark world in this 2-D arena game.  Use your smarts, your reflexes and a little luck to become the biggest shark in the water in this epic online multiplayer game. Try to grow your shark as you battle against all of the other sharks in the sea. Eat the smaller sharks as you try to grow to the biggest shark on the leaderboard. Have fun with SharkAttack.io',1,NULL,0,0,12,'Shark Attack.io.png',0,'https://sharkattack-io.com'),(44,'https://oceanar.io',0,'Oceanar.io is free-for-all io game in a battle to be the biggest fish in the sea! In this game you start off as a single angler fish and consume food to amass a fish army which will dominate the ocean! The games simple but incredibly addictive as its an awesome feeling to crush an opponents army with your own or take on that jelly fish that once killed you as a fresh spawn!',1,NULL,0,0,12,'Oceanar.io.png',0,'https://oceanar.io'),(45,'https://mineenergy.fun/',0,'MineEnergy.fun is a free online game.',1,NULL,0,0,15,'MineEnergy.fun.png',0,'https://mineenergy.fun/'),(46,'https://deadwalk.io',0,' ',1,NULL,0,0,15,'DeadWalk.io.png',0,'https://deadwalk.io'),(47,'http://fightz.io/?ref=iogamesspace',0,'Fightz.io is a multiplayer PvP battle arena. There are different places, each with own unique creatures and characteristics. Rabbits can be found anywhere, or you can choose to explore in the dangerous land of fire-breathing dragon. There are also different weapons in the game, from basic magic wand to powerful golden excalibur, to destructive flame throwing reaper.',1,NULL,0,0,12,'Fightz.io.png',0,'http://fightz.io/?ref=iogamesspace'),(48,'https://minesmashers.club',0,' ',1,NULL,0,0,12,'Minesmashers.club.png',0,'https://minesmashers.club'),(49,'https://digdig.io/',0,'digdig.io is a multiplayer io game made by the creator of agar.io and diep.io in which you dig to make yourself bigger. Currently there are two modes supported: FFA and Team Deathmatch. ',1,NULL,0,0,10,'digdig.io.png',0,'https://digdig.io/'),(50,'http://sharkz.io',0,'Sharkz.io is one of our favorite io games! You play in the ocean as a shark, and swim around, eating other creatures who are below you on the food chain. In the ocean, you will find plenty of other sharks, hammerheads, squid, a variety of fish, and many more sea creatures!',1,NULL,0,0,10,'Sharkz.io.png',0,'http://sharkz.io'),(51,'https://dotz.io/',0,'Dotz.io is a free-for-all io game where your goal is to become the biggest dot among others! Eat other dots to grow. But watch out: Each time you level up, your dot shrinks and you need to scale up even more! The bigger always eats the little. Can you become the biggest?',1,NULL,0,0,12,'Dotz.io.png',0,'https://dotz.io/'),(52,'http://play.treasurearena.com/',0,'Treasurearena.com is a fighting io game in which your goal is to defeat other players while scavaging for gold. This 16bit adventure game is incredibly fun! You’re stuck in a dungeon with 3 other players, you have the option of 3 different classes and you basically fight until the time limit is up! The player with the most gold collected by this time limit wins although killing others is a lot of fun and normally ensures victory regardless.',1,NULL,0,0,10,'Treasurearena.com.png',0,'http://play.treasurearena.com/'),(53,'https://blackhole-io.com',0,'Black Hole.io is a strategy io game where you\'re a black hole with a big appetite. Eat everything with your black hole and expand it to eat more! Enter the io arena and face the other holes in cool battle. Be biggest hole in town and have fun with Black Hole.io',1,NULL,0,0,12,'Black Hole.io.png',0,'https://blackhole-io.com'),(54,'https://superspin.io',0,'SuperSpin is a fun spinner io game where you spin and collect gems to grow. Can\'t sit still? Fingers have a life of their own? Do you find yourself blinking your eyes, flicking your fingers and looking for something occupy your mind?',1,NULL,0,0,12,'SuperSpin.io.png',0,'https://superspin.io'),(55,'https://www.modd.io/play/rich-city',0,' is a game in which you must try to survive a zombie apocalypse!',1,NULL,0,0,15,'Rich City.png',0,'https://www.modd.io/play/rich-city'),(56,'https://pie.ai',0,'Pie.AI is a funny free-for-all io game where you eat pies and fight your opponents.',1,NULL,0,0,12,'Pie.ai.png',0,'https://pie.ai'),(57,'https://embed.yorg3.io/iog',0,'YORG.io 3 is a unique combination of tower defense, zombies and strategy. It adds entirely new content over the original YORG.io, which was played by over 2 million people!',1,NULL,0,0,10,'YORG.io 3.png',0,'https://embed.yorg3.io/iog'),(58,'http://doomed.io/',0,'Doomed.io is a multiplayer survivalist io game where you need to live in the wilderness.',1,NULL,0,0,10,'Doomed.io.png',0,'http://doomed.io/'),(59,'http://orn.io',0,'Orn.io is a multiplayer 3D io game, where you start off as a rolling ball and consume smaller balls and players by rolling over them.',1,NULL,0,0,12,'Orn.io.png',0,'http://orn.io'),(60,'http://s0urce.io/',0,'s0urce.io (source io) is a free io game about hacking other players.',1,NULL,0,0,10,'s0urce.io.png',0,'http://s0urce.io/'),(61,'https://flyufo.io/',0,'FlyUFO.io is a free IOGame. Welcome to the wild and weird world of Flyufo.io, a new multiplayer action game about alien abductions, where you get to play as the aliens! Control an advanced spaceship equipped with a tractor beam, capable of dragging humans, cars, and even larger objects inside; grow by absorbing everything around you and converting it into energy; compete against other players in real-time and reach the top of the scoreboard by the end of the round.',1,NULL,0,0,10,'FlyUFO.io.png',0,'https://flyufo.io/'),(62,'https://combines.io/',0,'Combines.io is a free iogame. Welcome to an addicting multiplayer online IO game in which you control a huge truck on a farm collecting grain. Hop on your combine and start to drive around the field to harvest all the grain you can. Be careful, do your best to avoid crashing other vehicles, walls or other parts of your own vehicle.',1,NULL,0,0,10,'Combines.io.png',0,'https://combines.io/'),(63,'https://analytics.shapez.io/track/iog?target=web',0,'shapez.io is a game about building factories to automate the creation and combination of shapes. Deliver the requested, increasingly complex shapes to progress within the game and unlock upgrades to speed up your factory.',1,NULL,0,0,10,'shapez.io.png',0,'https://analytics.shapez.io/track/iog?target=web'),(64,'http://narwhale.io',0,'Narwhale.io is an action io game with an epic battle between the unicorns of the sea. Do you like Narwhals? Do you like sword fighting? So how about using a Narwhals horn as a sword, and choppin other Narwhals up?! The concept of this game is so incredibly simple but so much fun, I’ve been playing it for just over half an hour now and I’m already hooked! The arena size is reasonably small which means a whole ton of action, you’ll never get the chance to sit still with over 20 other players launching themselves at you.',1,NULL,0,0,10,'Narwhale.io.png',0,'http://narwhale.io'),(65,'https://killer-io.com',0,'Killer.io is new iogame where you are the maniac. Explore new adventures of killing and hiding bodies in this fun online murder game. Try to kill as many people at the Halloween party as possible. Try not to get caught!',1,NULL,0,0,10,'Killer.io.png',0,'https://killer-io.com'),(66,'https://facepunch.io',0,'FacePunch.io is an io boxing arena game where you must fight to be the champion. Check out the new smash hit io game: Facepunch.io Boxing Arena! Step onto the ring to knock out all champions with your brutal punches and devastating combos!',1,NULL,0,0,10,'Facepunch.io.png',0,'https://facepunch.io'),(67,'https://outlive.ai',0,'Outlive.ai is a free IOgame. Welcome to the incredibly creepy world of Outlive.ai, a game where you are a wormy little snake who is trying to stay alive and outslink the competition. It is one heck of a game and might seem a little familiar, but don\'t be fooled. Outlive.ai an all-original take on the classic snake concept. Outlive.ai is an incredibly fast-paced multiplayer game building on top of the classic snake concept, but bringing brand-new mechanics and power-ups. Your goal is surviving as long as possible by avoiding increasingly challenging bots and other players. We challenge you to outlive other players and become the biggest snake in the room. Good luck trying to outlive the AI though, rumor has it that this feat has never been accomplished!',1,NULL,0,0,11,'outlive.ai.png',0,'https://outlive.ai'),(68,'https://jefawk.com/game-files/nightwalkers/',0,'Nightwalkers.io is a zombie survival game set in the modern world. What makes it unique is the noise and the apocalypse mechanic. Most actions you make will create noise which in turn will alert zombies. After level 3 you encounter Apocalypses which just mean there will be a bunch of zombies out to get you.',1,NULL,0,0,15,'Nightwalkers.io.png',0,'https://jefawk.com/game-files/nightwalkers/'),(69,'https://battlestick.net',0,' is a crazy and casual fast-paced physics-based 2D stickman MMO where you fight others in an arena with several weapons to choose from!',1,NULL,0,0,10,'Battlestick.net.png',0,'https://battlestick.net'),(70,'https://creatur.io',0,'Creatur.io is a mass multiplayer browser game which takes concepts of Agar.io and Slither.io to the next level.',1,NULL,0,0,10,'Creatur.io.png',0,'https